import React from "react";
import FullScreenSection from "./FullScreenSection";
import { Box, Heading } from "@chakra-ui/react";
import Card from "./Card";

const projects = [
  {
    title: "HTML/CSS Project",
    description:
      "A peer review project using software development tools like HTML, CSS & Bootstrap to build attractive web pages and structured semantic data to control how websites appear to the end user.",
    getImageSrc: () => require("../images/LuckyShrub.png"),
  },
  {
    title: "UX/UI",
    description:
      "A peer review project using UX insights, creating a user persona & wireframe for features in Figma & turning into Minimum Viable Product (MVP) style prototype from mockups",
    getImageSrc: () => require("../images/LilLemon.png"),
  },
  {
    title: "HTML/CSS Portfolio",
    description:
      "A art portfolio creating simple responsive layout with effects & interactive particle system using HTML, CSS, vanilla Javascript & Bootstrap grid system",
    getImageSrc: () => require("../images/OldWebsite.png"),
  },
  {
    title: "R3F",
    description:
      " ",
    getImageSrc: () => require("../images/Scene.png"),
  },
];

const ProjectsSection = () => {
  return (
    <FullScreenSection
      backgroundColor="green"
      isDarkBackground
      p={8}
      alignItems="flex-start"
      spacing={8}
    >
      <Heading as="h1" id="projects-section">
        Featured Projects
      </Heading>
      <Box
        display="grid"
        gridTemplateColumns="repeat(2,minmax(0,1fr))"
        gridGap={8}
      >
        {projects.map((project) => (
          <Card
            key={project.title}
            title={project.title}
            description={project.description}
            imageSrc={project.getImageSrc()}
          />
        ))}
      </Box>
    </FullScreenSection>
  );
};

export default ProjectsSection;
