import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faLinkedin,
  faMedium,
  faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";
import { Link } from "react-scroll";

const socials = [
  {
    icon: faEnvelope,
    url: "mailto: hello@email.com",
  },
  {
    icon: faGithub,
    url: "https://github.com",
  },
  {
    icon: faLinkedin,
    url: "https://www.linkedin.com",
  },
  {
    icon: faMedium,
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    url: "https://stackoverflow.com",
  },
];

const Scrollable = ({render}) => {
  const [scrollUp, setScrollUp] = useState(false)
  useEffect(() =>{
      let prevVal = 0
      let newVal = 0
      const handleScroll = (e) => {
          newVal = window.pageYOffset
          if (prevVal - newVal < 0){
              setScrollUp(false)
          }else if (prevVal - newVal > 0){
              setScrollUp(true)
          }
          prevVal = newVal
      }
      window.addEventListener('scroll', handleScroll)
      return () =>{
          window.removeEventListener('scroll', handleScroll)
      }
  }, [])
  return render (scrollUp)

}
const Header = () => {
  const handleClick = (anchor) => () => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  return (
    <Scrollable render={(scrollUp) =>(
     <Box
      position="fixed"
      top={0}
      left={0}
      right={0}
      translateY={0}
      transitionProperty="transform"
      transitionDuration="0.3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
      className={`header${scrollUp && 'down'}`}
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
            {/* Add social media links based on the `socials` data */}
            <HStack spacing={8}>
              <a href="mailto: hello@email.com">
                <FontAwesomeIcon icon={faEnvelope} size="2x"/>
              </a>
              <a href={faGithub.url}>
                <FontAwesomeIcon icon={faGithub} size="2x"/>
              </a>
              <a href={faLinkedin.url}>
                <FontAwesomeIcon icon={faLinkedin} size="2x"/>
              </a>
              <a href={faMedium.url}>
                <FontAwesomeIcon icon={faMedium} size="2x"/>
              </a>
              <a href={faStackOverflow.url}>
                <FontAwesomeIcon icon={faStackOverflow} size="2x"/>
              </a>
            </HStack>
          </nav>
          <nav>
            <HStack spacing={8}>
              {/* Add links to Projects and Contact me section */}
              <Link href='/#projects' className='Projects' to='projects-section' activeClass='active' ><span>Projects</span></Link>
              <Link href='/#contact-me' className='ContactMe' to='contactme-section' activeClass='active'><span>Contact Me</span></Link>
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
    )}/>
  );
};
export default Header;
