import { Heading, HStack, Image, Text, VStack, Box} from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc, url }) => {
  //UI components from Chakra UI
  return(
  <VStack
    color="black"
    backgroundColor="white"
    cursor="pointer"
    borderRadius="xl" >
      <Image src={imageSrc} borderRadius='10px' objectFit='cover' alt=''/>
      <VStack spacing={4} p={4} alignItems="flex-start">
          <HStack justifyContent="space-between" alignItems="center">
            <Heading as='h3' size='md'>{title}</Heading>
          </HStack>
        <Text color="#64748b" fontSize="lg">{description}</Text>
        <HStack spacing={2} alignItems="center">
          <a href={url}><Text m={4} color='black'>See more<FontAwesomeIcon icon={faArrowRight} /></Text></a>
        </HStack>
      </VStack>
  </VStack>
  )
}

export default Card;
