import React from "react";
import { Avatar, Heading, VStack, WrapItem } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import Typical from "react-typical"

const greeting = "Hello, I am Rai!";
const bio1 = "A frontend developer";
const bio2 = "specialised in React";

// Implement the UI for the LandingSection component according to the instructions.
// Use a combination of Avatar, Heading and VStack components.
const LandingSection = () => (
  <FullScreenSection
    justifyContent="center"
    alignItems="center"
    isDarkBackground
    backgroundColor="#2A4365"
  >
<>
  <VStack spacing={10}>
    <WrapItem>
      <Avatar name='Pete' size='2xl' src='https://i.pravatar.cc/150?img=7' />
    </WrapItem>
    <Heading as='h4' size='md'>{greeting}</Heading>
    <Heading as='h1' size='4xl' noOfLines={1}>
      <Typical loop={2} steps={[
        "Creative coder",
        500,
        "3D artist",
        500,
       "Front-End Dev",
        500,
        "Web designer",
        500
        ]}
      />
      </Heading>
    {/*<Heading as='h1' size='4xl' noOfLines={1}>
      {bio2} {bio1}
      </Heading>*/}
  </VStack>
</>
  </FullScreenSection>
)

export default LandingSection;
