import { Heading, HStack, Image, Text, VStack, Box} from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc }) => {
  // Implement the UI for the Card component according to the instructions.
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.
  return(
  <VStack spacing={4}>
    <Box bg='white' borderRadius='10px' overflow='hidden'>
      <Image src={imageSrc} borderRadius='10px' objectFit='cover' alt=''/>
      <Box>
        <Heading m={4} as='h1' color='black'>{title}</Heading>
        <Text m={4} color='gray'>{description}</Text>
        <Text m={4} color='black'>See more<FontAwesomeIcon icon={faArrowRight} /></Text>
      </Box>
    </Box>
  </VStack>
  )
};

export default Card;
