import React, { useEffect, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faLinkedin,
  faMedium,
  faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";

const socials = [
  {
    icon: faEnvelope,
    url: "mailto: hello@example.com",
  },
  {
    icon: faGithub,
    url: "https://github.com",
  },
  {
    icon: faLinkedin,
    url: "https://www.linkedin.com",
  },
  {
    icon: faMedium,
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    url: "https://stackoverflow.com",
  },
];

const Header = () => {
  const headerTop = useRef();
  const [boxSize, setBoxSize] = useState({
    scrollY: 0,
    top: 0,
    translateY: 0,
  });
  const maxBoxTranslateY = -200;

  const handleClick = (anchor) => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  const getHeaderTop = (prevScrollY) => {
    return window.scrollY > prevScrollY ?
       -headerTop.current.clientHeight : 0;
  };

  const getHeaderTranslateY = (prevScrollY) => {
    return window.scrollY > prevScrollY ? maxBoxTranslateY : 0;
  };

  useEffect(() => {
    const handleScroll = (e) => {
      setBoxSize((prevState) => ({
        scrollY: window.scrollY,
        top: getHeaderTop(prevState.scrollY),
        translateY: getHeaderTranslateY(prevState.scrollY),
      }));
    };
    document.addEventListener("scroll", handleScroll);
    return () => {
      document.removeEventListener("scroll", handleScroll);
    };
  });

  return (
    <Box
      ref={headerTop}
      position="fixed"
      top={boxSize.top}
      left={0}
      right={0}
      translateY={boxSize.translateY}
      transitionProperty="transform"
      transitionDuration=".3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
            {socials &&
              socials.map((social) => (
                <a
                  key={social.url}
                  href={social.url}
                  style={{ marginRight: "25px" }}
                >
                  <FontAwesomeIcon icon={social.icon} size="2x" />
                </a>
              ))}
          </nav>
          <nav>
            <HStack spacing={8}>
              <a href="/#projects" onClick={() => handleClick("projects")}>
                Projects
              </a>
              <a href="/#contact-me" onClick={() => handleClick("contactme")}>
                Contact Me
              </a>
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
  );
};
export default Header;
