import React, {useRef, useState} from "react";
import { useFormik } from "formik";
import {
  Box,
  Button,
  FormControl,
  FormErrorMessage,
  FormLabel,
  Heading,
  Input,
  Select,
  Textarea,
  VStack,
} from "@chakra-ui/react";
import * as Yup from 'yup';
import FullScreenSection from "./FullScreenSection";
import useSubmit from "../hooks/useSubmit";
import {useAlertContext} from "../context/alertContext";
import emailjs from '@emailjs/browser';

const LandingSection = () => {
  const {response, submit} = useSubmit();
  const { onOpen } = useAlertContext();
  const [validated, setValidated] = useState(false);
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_aqeb3n4', 'template_ovtaalk', form.current, '-XNbiAgv4STzNE7Vq')
      .then((result) => {
        console.log(result.text)
        onOpen(response);
        setValidated(false);
        resetForm();
      }, (error) => {
          alert("There was a problem sending your message.");
      })
  }
  const handleSubmit = (event) =>{
    const form = event.currentTarget;
    if(form.checkValidity()===false){
      event.preventDefault();
      event.stopPropagation();
    }else{
      sendEmail(event)
    }
    setValidated(true);
  }

  const {values, handleBlur, handleChange, resetForm, errors, touched} = useFormik({
    initialValues: {
      Name:'',
      email:'',
      type:'hireMe',
      comment:''
    },
    onSubmit: (values, actions) => {
      submit(values)
      console.log(values)
      actions.setSubmitting(false)
  },
    validationSchema: Yup.object({
      Name: Yup.string().min(2).required('Please enter your name'),
      email: Yup.string().email('Please enter valid email').required('Please enter your email'),
      type: Yup.string().default('Other'),
      comment: Yup.string().required('Please enter your message').min( 25,'Minimum of 25 characters')
    })
  })
  return (
    <FullScreenSection
      isDarkBackground
      backgroundColor="#512DA8"
      py={16}
      spacing={8}
      justifyContent="center"
      alignItems="center"
    >
      <Box px={4} bgGradient= 'radial(transparent, black)' rounded='xl' display="grid" >
      <VStack  py={4} alignItems="flex-start">
        <Heading as="h1" id="contactme-section">
          Contact me
        </Heading>
        <Box rounded="md" w="100%">
          <form noValidate ref={form} validated={validated} onSubmit={handleSubmit}>
            <VStack spacing={4}>
              <FormControl isInvalid ={!!touched.Name && errors.Name}>
                <FormLabel htmlFor="Name">Name</FormLabel>
                <Input
                  id="Name"
                  name="Name"
                  placeholder="Enter your name"
                  value={values.Name}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  required
                />
                <FormErrorMessage>{errors.Name}</FormErrorMessage>
              </FormControl>
              <FormControl isInvalid ={!!touched.email && errors.email} >
                <FormLabel htmlFor="email">Email Address</FormLabel>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  placeholder="Enter your email"
                  value={values.email}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  required
                />
                <FormErrorMessage>{errors.email}</FormErrorMessage>
              </FormControl>
              <FormControl>
                <FormLabel htmlFor="type">Type of enquiry</FormLabel>
                <Select id="type" name="type" value={values.type} onChange={handleChange}>
                  <option value="hireMe">Freelance project proposal</option>
                  <option value="other">Other</option>
                </Select>
              </FormControl>
              <FormControl isInvalid ={!!touched.comment && errors.comment}>
                <FormLabel htmlFor="comment">Your message</FormLabel>
                <Textarea
                  id="comment"
                  name="comment"
                  height={250}
                  value={values.comment}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  required
                  placeholder="Enter your message"
                />
                <FormErrorMessage>{errors.comment}</FormErrorMessage>
              </FormControl>
              <Button className="Button" type="submit" bg='indigo' _hover={{bg:'#4700D8'}} width="full">
                Submit
              </Button>
            </VStack>
          </form>
        </Box>
      </VStack>
      </Box>
    </FullScreenSection>
  );
};

export default LandingSection;
