import { Heading, HStack, Image, Text, VStack, Box, Card} from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Cardy = ({ title, description, imageSrc }) => {
  // Implement the UI for the Card component according to the instructions.
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.
  return(
  <>
  <Card>
  <Box>
    <Image src={imageSrc} borderRadius='10px' objectFit='cover' alt=''/>
  </Box>
  <Box>
    <VStack>
      <Heading as='h1'>{title}</Heading>
      <Text mt={4}>{description}</Text>
      <p>See more<FontAwesomeIcon icon={faArrowRight} size='1x' /></p>
    </VStack>
  </Box>
  </Card>
  </>
  )
};

export default Cardy;
