import { Heading, HStack, Image, Text, VStack, Box} from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc, url }) => {
  //UI components from Chakra UI
  return(
  <HStack > 
  <VStack >
    <Box bg='white' borderRadius='10px' overflow='hidden'>
      <Image src={imageSrc} borderRadius='10px' objectFit='cover' alt=''/>
      <Box>
        <Heading m={4} as='h1' color='black'>{title}</Heading>
        <Text m={4} color='gray'>{description}</Text>
        <a href={url}><Text m={4} color='black'>See more<FontAwesomeIcon icon={faArrowRight} /></Text></a>
      </Box>
    </Box>
  </VStack>
  </HStack>
  )
}

export default Card;
